import { Component, DoCheck } from '@angular/core';
import { Task } from '../../models/task';

@Component({
  selector: 'app-todo-list',
  templateUrl: './todo-list.component.html',
  styleUrls: ['./todo-list.component.scss'],
})
export class TodoListComponent implements DoCheck{

  ngDoCheck(): void {
    if(Array.isArray(this.taskList))
      this.taskList.sort(
        (first, last) => Number(first.checked) - Number(last.checked)
      );
    localStorage.setItem('tarefas', JSON.stringify(this.taskList));
  }

  public taskList: Array<Task> = JSON.parse(localStorage.getItem("tarefas") || '[]');

  public deleteItem(event: number) {
    this.taskList.splice(event, 1);
  }

  public deleteAll() {
    const confirm = window.confirm('Tem certeza que deseja excluir tudo ?');
    if (confirm) {
      this.taskList = [];
    }
  }

  public setEmitTask(event: string) {
    this.taskList.push({ name: event, checked: false });
  }

  public validationInput(event: string, index: number) {
    if (!event.length) {
      const confirm = window.confirm('Tarefa está vazia, deseja deletar?');
      if (confirm) {
        this.deleteItem(index);
      }
    }
  }
}

<section [ngClass]="{'loading': !taskList.length}">
    <ul *ngIf="taskList.length">
        <li *ngFor="let item of taskList; let i = index">
            <input type="checkbox" [(ngModel)]="item.checked"/>
            <input type="text" [(ngModel)]="item.name" 
            [ngClass]="{checked: item.checked}"
            (keyup)="validationInput(item.name, i)"/>
            <button (click)="deleteItem(i)">
                <img width="15px" src="../../../../../assets/icons/54324.png" alt=""/>
            </button>
        </li>
    </ul>
</section>

<app-todo-input-add-items (emiteItemTask)="setEmitTask($event)"></app-todo-input-add-items>
<app-todo-button-delete-all (click)="deleteAll()" *ngIf="taskList.length"></app-todo-button-delete-all>
